    json: {
      ...lastRow,
      formUrl,
      qrUrl
    }
  }
];

// Read the webhook payload (node name must match your canvas exactly)
const wb = $item(0).$node["ğŸ“¥ Submit Attendance"].json.body || $item(0).$node["ğŸ“¥ Submit Attendance"].json;

// Values from the webhook, with sheet fallbacks
const MIT_ID     = (wb?.MIT_ID ?? "").trim() || "unknown";
const usedToken  = (wb?.token  ?? "").trim() || $json.token || "missing";

// issuedAt primarily from webhook; fallback to sheet's 'timestamp'
const issuedAtRaw =
  wb?.issuedAt              // from the webhook
  ?? wb?.issueAt            // (tolerate typo)
  ?? $json.issuedAt         // if you ever add it to the sheet
  ?? $json.timestamp        // current sheet column name
  ?? null;

// Submission time = when this Code node runs (i.e. webhook arrival time)
const submittedAt = new Date();

// Validate/parse issuedAt
if (!issuedAtRaw) {
  return [{
    json: {
      MIT_ID,
      token: usedToken,
      status: "NoMatch",
      note: "Missing issuedAt/timestamp"
    }
  }];
}
const tokenIssuedAt = new Date(issuedAtRaw);
if (isNaN(tokenIssuedAt)) {
  return [{
    json: {
      MIT_ID,
      token: usedToken,
      status: "NoMatch",
      note: `Bad date: ${issuedAtRaw}`
    }
  }];
}

// 60-second window
const diffSeconds = Math.round((submittedAt - tokenIssuedAt) / 1000);
const status = (diffSeconds >= 0 && diffSeconds <= 60) ? "Valid" : "Expired";

return [{
  json: {
    MIT_ID,
    token: usedToken,
    issuedAt: tokenIssuedAt.toISOString(),
    submittedAt: submittedAt.toISOString(),
    diffSeconds,
    status,
  }
}];

// =====================================================
// 1) REBUILD STREAMED TEXT FROM OLLAMA (robust + tolerant)
// =====================================================

// Safely parse JSON if possible
function safeParse(str) {
  try { return JSON.parse(str); } catch { return null; }
}

// Extract all "response" fields from streamed Ollama chunks
function extractResponsesFromString(s) {
  if (typeof s !== "string" || !s.trim()) return "";
  let collected = "";

  // Single JSON line (e.g., {"response":"..."})
  const single = safeParse(s.trim());
  if (single && typeof single === "object" && "response" in single) {
    collected += String(single.response || "");
    return collected;
  }

  // Multiple concatenated JSON blocks (Ollama stream style)
  const matches = s.match(/{[^]*?}(?=\s*{|\s*$)/g) || [];
  for (const m of matches) {
    const obj = safeParse(m);
    if (obj && typeof obj === "object" && "response" in obj) {
      collected += String(obj.response || "");
    }
  }

  // If nothing parsed, return raw string
  if (!collected && s) collected += s;
  return collected;
}

let fullText = "";

// Collect and merge text from all incoming items
for (const item of $input.all()) {
  const j = item.json;

  if (typeof j?.data === "string") {
    fullText += extractResponsesFromString(j.data);
    continue;
  }
  if (typeof j?.summary === "string") {
    fullText += extractResponsesFromString(j.summary);
    continue;
  }
  if (typeof j === "object" && j && "response" in j) {
    fullText += String(j.response || "");
    continue;
  }
  if (typeof j === "string") {
    fullText += extractResponsesFromString(j);
    continue;
  }
  if (j != null) {
    fullText += extractResponsesFromString(JSON.stringify(j));
  }
}

// Clean and normalize
fullText = String(fullText || "")
  .replace(/\\n/g, "\n")         // render escaped newlines
  .replace(/\r/g, "")
  .replace(/[ \t]{2,}/g, " ")    // collapse extra spaces
  .replace(/[{}]+/g, "")         // remove stray braces
  .replace(/\s*\n\s*/g, "\n")    // trim newlines
  .trim();


// =====================================================
// 2) EXTRACT SECTIONS + ID COUNTS
// =====================================================

// Split into valid/expired sections
const validSection = fullText.match(/âœ…[\s\S]*?(?=\n\s*âŒ|$)/);
const expiredSection = fullText.match(/âŒ[\s\S]*?$/);

// Count MIT-style (or similar) IDs
function extractIDs(section) {
  if (!section) return [];
  const ids = section[0].match(/\b[A-Z]{2,}\d{3,}\b/g) || [];
  return [...new Set(ids)]; // unique
}

const validMembers = extractIDs(validSection);
const expiredMembers = extractIDs(expiredSection);

const validCount = validMembers.length;
const expiredCount = expiredMembers.length;
const totalCount = validCount + expiredCount;


// =====================================================
// 3) OPTIONAL â€” DATE EXTRACTION
// =====================================================
let summaryDate = "";
const dateMatch = fullText.match(/for\s+([A-Za-z]+\s+\d{1,2}(?:st|nd|rd|th)?,?\s*\d{4})/i);
if (dateMatch) {
  summaryDate = dateMatch[1].trim();
} else {
  summaryDate = new Date().toLocaleDateString("en-US", {
    year: "numeric", month: "long", day: "numeric"
  });
}


// =====================================================
// 4) RETURN FINAL OBJECT
// =====================================================
return [
  {
    json: {
      summary: fullText,
      summaryDate,
      validCount,
      expiredCount,
      totalCount,
      validMembers,
      expiredMembers
    }
  }
];

