// --- Simple deterministic hash (FNV-1a) ---
function simpleHash(str) {
  let hash = 2166136261;
  for (let i = 0; i < str.length; i++) {
    hash ^= str.charCodeAt(i);
    hash += (hash << 1) + (hash << 4) + (hash << 7) + (hash << 8) + (hash << 24);
  }
  return ("0000000" + (hash >>> 0).toString(16)).slice(-8);
}

// --- Extract nested data safely ---
const data =
  $json.receivedData?.body ||
  $json.body ||
  $json || {};

// --- Parse and clean fields ---
const summaryDate = data.summaryDate || "N/A";
const validCount = data.validCount || "0";
const expiredCount = data.expiredCount || "0";
const validMembers = (data.validMembers || "").split(",").map(x => x.trim()).filter(Boolean);
const expiredMembers = (data.expiredMembers || "").split(",").map(x => x.trim()).filter(Boolean);

// --- Canonical string ---
const summaryText = `${summaryDate}|${validCount}|${expiredCount}|${validMembers.join(",")}|${expiredMembers.join(",")}`;

// --- Short hash ---
const summaryHash = simpleHash(summaryText);

// --- Return clean structured output ---
return [
  {
    json: {
      summaryHash,
      summaryText,
      summaryDate,
      validCount,
      expiredCount,
      validMembers,
      expiredMembers,
    },
  },
];

// Get all rows from previous node
const rows = $input.all();

// Group students by status
const valid = [];
const expired = [];

for (const item of rows) {
  const data = item.json;
  if (data.status === 'Valid') {
    valid.push(data.MIT_ID);
  } else if (data.status === 'Expired') {
    expired.push(data.MIT_ID);
  }
}

// Build a summary prompt
const summary = `
Daily Attendance Summary:

âœ… VALID (${valid.length}):
${valid.join(', ') || 'None'}

âŒ EXPIRED (${expired.length}):
${expired.join(', ') || 'None'}

Generated at: ${new Date().toISOString()}
`;

return [
  {
    json: {
      prompt: summary,
      validCount: valid.length,
      expiredCount: expired.length,
      validList: valid,
      expiredList: expired
    }
  }
];

// Get the raw outputs
const attendance = $items("ğŸ§® Compute Hash")[0].json;
const execOutput = $json;

// Extract and parse the blockchain JSON from stdout
const match = execOutput.stdout.match(/{[\s\S]*}$/); // find last JSON object
const blockchain = match ? JSON.parse(match[0]) : {};

// Combine attendance + blockchain data
return [
  {
    json: {
      summaryDate: attendance.summaryDate,
      summaryHash: attendance.summaryHash,
      summaryText: attendance.summaryText,
      validCount: attendance.validCount,
      expiredCount: attendance.expiredCount,
      validMembers: attendance.validMembers,
      expiredMembers: attendance.expiredMembers,
      txHash: blockchain.txHash || null,
      blockNumber: blockchain.blockNumber || null,
      gasUsed: blockchain.gasUsed || null,
      timestamp: blockchain.timestamp || null,
    },
  },
];

